const sectionTopContent=document.querySelector(".section-top__content"),sectionBottom=document.querySelector(".section-bottom"),sectionBottomDetails=document.querySelector(".section-bottom .details"),quoteBlock=document.querySelector(".quote-block"),greeting=document.querySelector(".greeting"),cityAndCountry=document.querySelector(".location"),time=document.querySelector(".time"),fetchAppData=async()=>{try{const[t,e]=await axios.all([await axios.get("https://ipapi.co/json/"),await axios.get("https://quotes.rest/qod?language=en")]),n=t.data;sectionTopHTML(e.data.contents.quotes[0],n),sectionBottomHTML(n)}catch(t){greeting.innerHTML='\n      <p class="flex">\n         Something went wrong <img src="imgs/crying.gif" class="crying-gif" />\n      </p>'}};fetchAppData();let timeOfDay,dayIcon,detailsTitleColor="light-mode-font-color";function checkTime(t){const e="linear-gradient(rgba(0, 0, 0, 0.65), rgba(0, 0, 0, 0.5))";t<12?(timeOfDay="morning",dayIcon="cloud-sun"):t<18?(timeOfDay="afternoon",dayIcon="sun",document.body.style.backgroundImage=`${e}, url('imgs/afternoon.jpg')`):(timeOfDay="evening",dayIcon="moon",sectionBottom.style.backgroundColor="rgba(0, 0, 0, .8)",sectionBottom.style.color="rgb(222, 227, 231)",detailsTitleColor="dark-mode-font-color",document.body.style.backgroundImage=`${e}, url('imgs/evening.jpg')`)}const currentDate=new Date,firstDayOfYear=new Date(currentDate.getFullYear(),0,1),[hoursOfDay]=getTime().split(":");function updateTime(){setInterval(function(){time.innerHTML=getTime()},1e3)}function sectionTopHTML(t,e){quoteBlock.innerHTML=`\n            <div class="quote-block">\n               <q>${t.quote}</q>\n               <cite class="block bold mt-1">&mdash; ${t.author}</cite>\n               <small>source: <a href="${t.permalink}" rel="noreferrer" class="quoteSource" target="_blank">\n                  theysaidso.com\n               </a></small>\n            </div>\n            `,greeting.innerHTML=`<p class="flex"><i class="icon mr-1 las la-${dayIcon}"></i> Good ${timeOfDay}, It's Currently</p>`,cityAndCountry.innerHTML=`<p class="location">IN ${e.city}, ${e.country_name}</p>`}function sectionBottomHTML(t){sectionBottomDetails.innerHTML=`\n         <div class="details__content">\n            <div class="details__content--title ${detailsTitleColor}">CURRENT TIME ZONE</div>\n            <div class="details__content--info">${t.timezone}</div>\n         </div>\n         <div class="details__content">\n            <div class="details__content--title ${detailsTitleColor}">DAY OF THE YEAR</div>\n            <div class="details__content--info">${dayOfTheYear()}</div>\n         </div>\n         <div class="details__content">\n            <div class="details__content--title ${detailsTitleColor}">DAY OF THE WEEK</div>\n            <div class="details__content--info">${currentDate.getDay()+1}</div>\n         </div>\n         <div class="details__content">\n            <div class="details__content--title ${detailsTitleColor}">WEEK NUMBER</div>\n            <div class="details__content--info">${getWeekNumber()}</div>\n         </div>\n      `}function formatTime(t){return t<10?`0${t}`:t}function getTime(){return`${formatTime(currentDate.getHours())}:${formatTime(currentDate.getMinutes())}`}function dayOfTheYear(){return Math.ceil((currentDate-firstDayOfYear)/864e5)}function getWeekNumber(){return Math.ceil((currentDate.getDay()+1+dayOfTheYear())/7)}checkTime(hoursOfDay),updateTime();